import{_ as e,c,o as d,a as o}from"./app.fe572a1f.js";const B=JSON.parse('{"title":"TCP","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E09\u6B21\u63E1\u624B","slug":"\u4E09\u6B21\u63E1\u624B","link":"#\u4E09\u6B21\u63E1\u624B","children":[]},{"level":2,"title":"\u6570\u636E\u4F20\u8F93","slug":"\u6570\u636E\u4F20\u8F93","link":"#\u6570\u636E\u4F20\u8F93","children":[]},{"level":2,"title":"\u56DB\u6B21\u6325\u624B","slug":"\u56DB\u6B21\u6325\u624B","link":"#\u56DB\u6B21\u6325\u624B","children":[]}],"relativePath":"web-basics/network/tcp.md","lastUpdated":1670155339000}'),i={name:"web-basics/network/tcp.md"},a=o('<h1 id="tcp" tabindex="-1">TCP <a class="header-anchor" href="#tcp" aria-hidden="true">#</a></h1><h2 id="\u4E09\u6B21\u63E1\u624B" tabindex="-1">\u4E09\u6B21\u63E1\u624B <a class="header-anchor" href="#\u4E09\u6B21\u63E1\u624B" aria-hidden="true">#</a></h2><p>\u8FD9\u91CC\u5047\u8BBE A(client) \u4EE5\u53CA B(server)</p><p>A\u548CB\u63E1\u624B\u8FC7\u7A0B:</p><ol><li>A \u53D1\u9001 <code>syn</code> \u62A5\u6587\u7ED9B ( \u6B64\u65F6A\u72B6\u6001\u4E3A <code>syn_sent</code> )</li><li>B \u6536\u5230 <code>syn</code> \u62A5\u6587,\u5E76\u53D1\u9001 <code>ack + syn</code> \u62A5\u6587\u7ED9 A ( \u6B64\u65F6B\u72B6\u6001\u4E3A <code>syn_rcvd</code> )</li><li>A \u53D1\u9001 <code>ack</code> \u62A5\u6587\u7ED9B ( <code>ack</code>\u53EF\u4EE5\u7406\u89E3\u4E3A\u786E\u5B9A\u6536\u5230\u4E86\u7684\u610F\u601D )</li></ol><p>syn_sent\u548Csyn_rcvd,\u8FD9\u4E24\u4E2A\u72B6\u6001\u53EB\u7740\u300C\u534A\u6253\u5F00\u300D\u72B6\u6001,\u5C31\u662F\u5411\u5BF9\u65B9\u62DB\u624B\u4E86,\u4F46\u662F\u8FD8\u6CA1\u5F97\u5230\u5BF9\u65B9\u56DE\u5E94(rcvd = received)</p><ul><li>syn_sent\u662F\u4E3B\u52A8\u6253\u5F00\u65B9\u7684\u300C\u534A\u6253\u5F00\u300D\u72B6\u6001</li><li>syn_rcvd\u662F\u88AB\u52A8\u6253\u5F00\u65B9\u7684\u300C\u534A\u6253\u5F00\u300D\u72B6\u6001</li><li>\u8FD9\u91CCA\u662F\u4E3B\u52A8\u6253\u5F00\u65B9,B\u662F\u88AB\u52A8\u6253\u5F00\u65B9</li></ul><h2 id="\u6570\u636E\u4F20\u8F93" tabindex="-1">\u6570\u636E\u4F20\u8F93 <a class="header-anchor" href="#\u6570\u636E\u4F20\u8F93" aria-hidden="true">#</a></h2><p>\u63A5\u7740A\u548CB\u5F00\u59CB\u4E92\u76F8\u4F20\u9012\u6570\u636E,\u4F1A\u4E00\u76F4\u91CD\u590D\u7740\u8FD9\u6837\u7684\u573A\u666F:<br> A\u53D1\u9001data\u7ED9B,B\u63A5\u6536\u5230\u540E\u9700\u8981\u56DE\u590DA <code>ack</code> \u62A5\u6587,\u8868\u793A\u5DF2\u6536\u5230\u6570\u636E</p><ul><li><p>tcp\u91CD\u4F20 \u5728\u4E0A\u9762\u7684\u573A\u666F\u4E2D,\u5982\u679CA\u53D1\u9001\u7ED9B\u7684\u6570\u636E\u4E22\u5931\u4E86\u6216\u8005A\u6CA1\u6709\u63A5\u6536\u5230B\u7684\u56DE\u590D,\u90A3\u4E48A\u4E0D\u7BA1\u662F\u53D1\u751F\u4E86\u4EC0\u4E48\u90FD\u4F1A\u91CD\u65B0\u4F20\u4E00\u904D\u7ED9B</p></li><li><p>tcp\u53BB\u91CD \u63A5\u7740\u4E0A\u9762\u7684\u91CD\u4F20\u573A\u666F,\u90A3\u4E48A\u6709\u53EF\u80FD\u4F1A\u53D1\u9001\u4E24\u6B21\u6570\u636E\u7ED9B,\u8FD9\u6837\u5C31\u4F1A\u6709\u6570\u636E\u91CD\u590D\u4E86,\u4E0D\u8FC7 <code>\u91CD\u4F20 &amp; \u53BB\u91CD</code> \u7684\u5DE5\u4F5C\u64CD\u4F5C\u7CFB\u7EDF\u7F51\u7EDC\u6A21\u5757\u5DF2\u7ECF\u5E2E\u6211\u4EEC\u5904\u7406\u597D\u4E86</p></li><li><p>\u6279\u91CFack A\u5982\u679C\u8FDE\u7EED\u53D1\u5F88\u591A\u6B21\u6570\u636E\u7ED9B,B\u4E5F\u4E0D\u4F1A\u6BCF\u4E2A\u90FD\u56DE\u4E00\u53E5 <code>ack</code>,\u5B83\u4F1A\u7EDF\u4E00\u56DE\u590D\u4E00\u6B21 <code>ack</code> \u8868\u793A\u6536\u5230\u6570\u636E</p></li><li><p>tcp\u7A97\u53E3\u5927\u5C0F \u4F46\u662FA\u4E0D\u80FD\u4E00\u6B21\u53D1\u592A\u591A\u6570\u636E,A\u548CB\u8981\u4FDD\u6301\u4E00\u5B9A\u7684\u53D1\u9001\u548C\u63A5\u6536\u9891\u7387,\u8FD9\u4E2A\u53EB\u505A <code>tcp</code> \u7A97\u53E3\u5927\u5C0F</p></li></ul><h2 id="\u56DB\u6B21\u6325\u624B" tabindex="-1">\u56DB\u6B21\u6325\u624B <a class="header-anchor" href="#\u56DB\u6B21\u6325\u624B" aria-hidden="true">#</a></h2><p>\u6325\u624B\u8FC7\u7A0B:</p><ol><li>A \u53D1\u9001 <code>fin</code> \u62A5\u6587\u7ED9B (\u6B64\u65F6A\u72B6\u6001\u4E3A <code>fin_wait_1</code>)</li><li>B \u53D1\u9001 <code>ack</code> \u62A5\u6587\u7ED9A (\u6B64\u65F6A\u72B6\u6001\u4E3A <code>fin_wait_2</code>, B\u72B6\u6001\u4E3A <code>close_wait</code>)</li><li>B \u53D1\u9001 <code>fin</code> \u62A5\u6587\u7ED9A (\u6B64\u65F6A\u72B6\u6001\u4E3A <code>time_wait</code>, B\u72B6\u6001\u4E3A <code>last_ack</code> )</li><li>A \u53D1\u9001 <code>ack</code> \u62A5\u6587\u7ED9B (\u6B64\u65F6B\u72B6\u6001\u4E3A <code>close</code>)</li></ol><p>\u8FD9\u91CCtcp\u5B58\u5728\u534A\u5173\u95ED\u72B6\u6001,\u4E5F\u5C31\u662F\u5355\u5411\u5173\u95ED,\u6BD4\u5982A\u5DF2\u7ECF\u53D6\u6D88\u53D1\u9001\u4E86,\u4F46\u662F\u4E0D\u4F1A\u53D6\u6D88\u63A5\u6536,B\u53EF\u4EE5\u63A5\u7740\u53D1\u6CA1\u6709\u53D1\u9001\u5B8C\u7684\u6570\u636E,\u7B49B\u4E5F\u4E0D\u53D1\u9001\u6570\u636E\u4E14\u56DE\u590DA <code>ack</code> \u62A5\u6587,\u90A3\u4E48\u624D\u4EE3\u8868\u5F7B\u5E95\u7ED3\u675F</p><ul><li><p><code>time_wait</code>\u72B6\u6001\u89E3\u91CA:<br> \u662F\u4E3B\u52A8\u5173\u95ED\u7684\u4E00\u65B9\u5728\u63A5\u6536\u5230\u5BF9\u65B9\u6325\u624B\u540E\u624D\u8FDB\u5165\u7684\u957F\u671F\u72B6\u6001,\u65F6\u957F\u4E3A4\u5206\u949F,4\u5206\u949F\u8FC7\u540E\u4E3B\u52A8\u5173\u95ED\u7684\u4E00\u65B9\u624D\u8FDB\u5165 <code>close</code> \u72B6\u6001,\u5B83\u7684\u4F5C\u7528\u662F\u91CD\u4F20\u6700\u540E\u4E00\u4E2A <code>ack</code> \u62A5\u6587,\u786E\u4FDD\u5BF9\u65B9\u6536\u5230,\u5BF9\u65B9\u6CA1\u6709\u6536\u5230\u5219\u4F1A\u91CD\u4F20 <code>fin</code> \u62A5\u6587</p></li><li><p>4\u5206\u949F\u89E3\u91CA:<br> 4\u5206\u949F\u662F2\u4E2AMSL,MSL\u662F\u6700\u957F\u62A5\u6587\u5BFF\u547D,\u6570\u636E\u7684\u6765\u56DE\u4E00\u4E0B\u5C31\u662F\u4E24\u4E2A\u4E86</p></li></ul><p>\u672C\u7BC7\u53C2\u8003\u6B64\u535A\u6587 <a href="https://juejin.cn/post/6844903625513238541#comment" target="_blank" rel="noreferrer">https://juejin.cn/post/6844903625513238541#comment</a></p>',16),t=[a];function l(n,r,s,p,_,h){return d(),c("div",null,t)}const k=e(i,[["render",l]]);export{B as __pageData,k as default};
